<?php

namespace Hexmedia\ContentBundle\Repository\Doctrine;

use Doctrine\ORM\NoResultException;
use Doctrine\ORM\ORMException;
use Hexmedia\AdministratorBundle\Repository\Doctrine\ListTrait;
use Hexmedia\AdministratorBundle\Repository\Doctrine\TranslationTrait;
use Hexmedia\ContentBundle\Repository\PageRepositoryInterface;
use Hexmedia\AdministratorBundle\Repository\Doctrine\CrudRepository;

/**
 * PageRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PageRepository extends CrudRepository implements PageRepositoryInterface
{
    use ListTrait;
    use TranslationTrait;

    /**
     * {@inheritdoc}
     */
    public function createListQueryBuilder($alias)
    {
        return $this->createQueryBuilder($alias)->where($alias . ".special = 0");
    }
}
